var pmx = require("pmx");
var probe = pmx.probe();

module.exports = {
  minersCounter: probe.counter({
    name: "Miners"
  }),
  connectionsCounter: probe.counter({
    name: "Connections"
  }),
  sharesCounter: probe.counter({
    name: "Shares"
  }),
  sharesMeter: probe.meter({
    name: "Shares per minute",
    samples: 60
  })
}
